DROP DATABASE IF EXISTS `is1-s3`;
CREATE DATABASE `is1-s3`;
USE `is1-s3`;

CREATE TABLE `place` (
    `id` INTEGER PRIMARY KEY AUTO_INCREMENT,
    `postal_code` INTEGER NOT NULL,
    `name` VARCHAR(255) NOT NULL
) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci;
CREATE TABLE `client` (
    `id` INTEGER PRIMARY KEY AUTO_INCREMENT,
    `name` VARCHAR(255) NOT NULL,
    `address` VARCHAR(255) NOT NULL,
    `place_id` INTEGER NOT NULL,
    FOREIGN KEY (`place_id`) REFERENCES `place` (`id`)
        ON DELETE CASCADE
        ON UPDATE NO ACTION
) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci;
CREATE TABLE `office` (
    `id` INTEGER PRIMARY KEY AUTO_INCREMENT,
    `name` VARCHAR(255) NOT NULL,
    `address` VARCHAR(255) NOT NULL,
    `place_id` INTEGER NOT NULL,
    FOREIGN KEY (`place_id`) REFERENCES `place` (`id`)
        ON DELETE CASCADE
        ON UPDATE NO ACTION
) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci;
CREATE TABLE `account` (
    `id` INTEGER PRIMARY KEY AUTO_INCREMENT,
    `balance` INTEGER NOT NULL DEFAULT 0,
    `overdraft` INTEGER NOT NULL,
    `creation_date` DATETIME NOT NULL DEFAULT NOW(),
    `transaction_count` INTEGER NOT NULL DEFAULT 0,
    `client_id` INTEGER NOT NULL,
    `place_id` INTEGER NOT NULL,
    FOREIGN KEY (`client_id`) REFERENCES `client` (`id`)
        ON DELETE CASCADE
        ON UPDATE NO ACTION,
    FOREIGN KEY (`place_id`) REFERENCES `place` (`id`)
        ON DELETE CASCADE
        ON UPDATE NO ACTION
) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci;
CREATE TABLE `transaction` (
    `id` INTEGER PRIMARY KEY AUTO_INCREMENT,
    `type` ENUM('incoming', 'outgoing', 'internal') NOT NULL,
    `processing_date` DATETIME NOT NULL DEFAULT NOW(),
    `amount` INTEGER NOT NULL,
    `transaction_number` INTEGER NOT NULL,
    `purpose` VARCHAR(255) NULL,
    `account_id_from` INTEGER NOT NULL,
    `account_id_to` INTEGER NOT NULL,
    `office_id` INTEGER NULL,
    FOREIGN KEY (`account_id_from`) REFERENCES `account` (`id`)
        ON DELETE CASCADE
        ON UPDATE NO ACTION,
    FOREIGN KEY (`account_id_to`) REFERENCES `account` (`id`)
        ON DELETE CASCADE
        ON UPDATE NO ACTION,
    FOREIGN KEY (`office_id`) REFERENCES `office` (`id`)
        ON DELETE CASCADE
